import {HeroSection} from "@/app/careers/[country]/_components/hero-section";
import {Form} from "@/app/careers/[country]/compensation-estimator/_components/form";

export default function CompensationEstimatorPage() {
    return (
        <main>
            <HeroSection/>
            <Form/>
        </main>
    )
}

import {z} from "zod";

const Schema = z.object({
    firstName: z.string()
        .min(2, {
            message: "First name must be at least 2 characters long"
        })
        .max(50, {
            message: "First name must be less than 50 characters long"
        }),
    lastName: z.string()
        .min(2, {message: "Last name must be at least 2 characters long"})
        .max(50, {message: "Last name must be less than 50 characters long"}),
    email: z.string().email(),
    state: z.string().min(1, {
        message: "Please select a state"
    }),
    county: z.string().min(1, {
        message: "Please select a county"
    }),
    role: z.string().min(1, {
        message: "Please select a role"
    })
})

export function Form() {


    return (
        <section className="relative flex justify-center">
            <div
                className="relative z-10 flex flex-col gap-8 lg:gap-12 items-center py-12 px-5 lg:p-20 w-full max-w-[1440px] backdrop-blur-[10px] bg-white/70">


            </div>
        </section>
    )
}

