import Image from 'next/image';

interface HeroSectionProps {
    caption?: string;
    headline?: string;
    backgroundImage?: string;
}

export function HeroSection({
    caption = 'Careers at Team PBS - united states',
    headline = 'Team PBS - opportunities for development and growth',
    backgroundImage = '/images/careers/careers-hero.jpg',
}: HeroSectionProps) {
    return (
        <section className="relative flex flex-col items-center h-[500px] lg:h-[800px] mb-[-150px] lg:mb-[-294px] pb-20 pt-40 lg:pt-60 px-5 lg:px-20 overflow-clip">
            {/* Background Image */}
            <div className="absolute inset-0 pointer-events-none">
                <div className="absolute inset-0 overflow-hidden">
                    <Image
                        src={backgroundImage}
                        alt=""
                        fill
                        priority
                        className="object-cover object-center"
                        aria-hidden="true"
                    />
                </div>
                {/* Gradient Overlays */}
                <div
                    className="absolute inset-0"
                    style={{
                        backgroundImage:
                            'linear-gradient(180deg, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0) 100%), linear-gradient(90deg, rgba(0, 0, 0, 0.1) 0%, rgba(0, 0, 0, 0.1) 100%)',
                    }}
                />
            </div>

            {/* Content */}
            <div className="relative z-10 flex flex-col gap-12 items-start w-full max-w-[900px]">
                <div className="flex flex-col gap-2 items-center w-full text-center uppercase">
                    <p className="text-secondary text-base lg:text-xl font-bold tracking-[1px] drop-shadow-sm">
                        {caption}
                    </p>
                    <h1 className="text-3xl md:text-4xl lg:text-[54px] font-bold text-white leading-[1.1] drop-shadow-sm">
                        {headline}
                    </h1>
                </div>
            </div>
        </section>
    );
}

