import Link from 'next/link';
import {ArrowRight} from 'lucide-react';
import {PiggyBank} from '@/shared/icones/piggy-bank';
import {Car} from '@/shared/icones/car';
import {Healthcare} from '@/shared/icones/healthcare';
import {MoneyWavy} from '@/shared/icones/money-wavy';
import {GraduationCap} from '@/shared/icones/graduation-cap';
import {Mentorship} from '@/shared/icones/mentorship';
import {LineCharts} from '@/shared/icones/line-charts';
import {TechnologyTools} from '@/shared/icones/technology-tools';
import {ComponentType, SVGProps} from 'react';
import {CertificateRibbon} from "@/shared/icones/certificate-ribbon";

interface BenefitItem {
    icon: ComponentType<SVGProps<SVGSVGElement>>;
    label: string;
}

interface BenefitsSectionProps {
    headline?: string;
    description?: string;
    benefits?: BenefitItem[];
    ctaText?: string;
    ctaHref?: string;
}

const defaultBenefits: BenefitItem[] = [
    {icon: PiggyBank, label: '401K with Match'},
    {icon: Car, label: 'Paid Drive-Time'},
    {icon: Healthcare, label: 'Healthcare Plans'},
    {icon: MoneyWavy, label: 'Competitive Salary'},
    {icon: GraduationCap, label: 'PBS University Partnerships'},
    {icon: Mentorship, label: 'PBS Mentor Program for Student Analysts'},
    {icon: CertificateRibbon, label: "Free CEU's"},
    {icon: LineCharts, label: 'Professional Development'},
    {icon: TechnologyTools, label: 'Advanced ABA Technology Tools'},
];

export function BenefitsSection({
                                    headline = 'Benefits',
                                    description = "These are just a few of the benefits you'll enjoy  and there's so much more waiting for you.",
                                    benefits = defaultBenefits,
                                    ctaText = 'Compensation Estimator',
                                    ctaHref = '/careers/us/compensation-estimator',
                                }: BenefitsSectionProps) {
    return (
        <section className="bg-[#f5f7f9]">
            <div
                className="flex flex-col gap-12 lg:gap-16 items-center pb-20 lg:pb-32 pt-10 px-6 lg:px-20 max-w-[1440px] mx-auto">
                {/* Section Text */}
                <div className="flex flex-col gap-8 lg:gap-12 items-start w-full">
                    <div className="flex flex-col gap-2 items-center w-full">
                        <h2 className="text-3xl md:text-4xl lg:text-[54px] font-bold text-[#021b24] text-center uppercase leading-[1.1] w-full">
                            {headline}
                        </h2>
                    </div>
                    <p className="text-base lg:text-lg font-normal text-[#021b24] text-center leading-[1.4] w-full">
                        {description}
                    </p>
                </div>

                {/* Benefits Grid */}
                <div className="flex flex-wrap gap-9 items-start justify-center max-w-[900px] w-full">
                    {benefits.map((benefit, index) => (
                        <div
                            key={index}
                            className="flex flex-col flex-1 gap-4 items-center min-w-[200px] px-4"
                        >
                            <div
                                className="flex items-center justify-center w-[100px] h-[100px] lg:w-[120px] lg:h-[120px] bg-primary rounded-full overflow-clip">
                                <benefit.icon className="w-[50px] h-[50px] lg:w-[60px] lg:h-[60px]"/>
                            </div>
                            <p className="text-lg lg:text-xl font-bold text-[#021b24] text-center tracking-[0.5px]">
                                {benefit.label}
                            </p>
                        </div>
                    ))}
                </div>

                {/* CTA Button */}
                <Link
                    href={ctaHref}
                    className="flex items-center justify-center h-14 px-6 py-4 border-2 border-[#021b24]/80 rounded-full group hover:bg-[#021b24]/5 transition-colors"
                >
                    <span className="px-2 text-lg lg:text-xl font-semibold text-[#021b24]/80 tracking-[0.5px]">
                        {ctaText}
                    </span>
                    <ArrowRight className="w-5 h-5 text-[#021b24] group-hover:translate-x-1 transition-transform"/>
                </Link>
            </div>
        </section>
    );
}

