"use client";

import {z} from "zod";
import {useState} from "react";
import Link from "next/link";
import Image from "next/image";
import {ArrowLeft, ChevronDown} from "lucide-react";
import {User} from "@/shared/icones/user";
import {Registered} from "@/shared/icones/registered";
import {Tie} from "@/shared/icones/tie";
import {GraduationCap} from "@/shared/icones/graduation-cap";
import {Clock} from "@/shared/icones/clock";
import {CoinsHand} from "@/shared/icones/coins-hand";
import {Umbrella} from "@/shared/icones/umbrella";
import {Thermometer} from "@/shared/icones/thermometer";
import {MedicalCross} from "@/shared/icones/medical-cross";
import {ShieldPlus} from "@/shared/icones/shield-plus";
import {TrendUp} from "@/shared/icones/trend-up";
import {Calculator} from "@/shared/icones/calculator";
import {Gift} from "@/shared/icones/gift";
import {MoneyWavy} from "@/shared/icones/money-wavy";
import {PiggyBank} from "@/shared/icones/piggy-bank";

const Schema = z.object({
    firstName: z.string()
        .min(2, {message: "First name must be at least 2 characters long"})
        .max(50, {message: "First name must be less than 50 characters long"}),
    lastName: z.string()
        .min(2, {message: "Last name must be at least 2 characters long"})
        .max(50, {message: "Last name must be less than 50 characters long"}),
    email: z.string().email(),
    state: z.string().min(1, {message: "Please select a state"}),
    county: z.string().min(1, {message: "Please select a county"}),
    role: z.string().min(1, {message: "Please select a role"})
});

type FormData = z.infer<typeof Schema>;

const states = [
    "Alabama", "Alaska", "Arizona", "Arkansas", "California", "Colorado", "Connecticut",
    "Delaware", "Florida", "Georgia", "Hawaii", "Idaho", "Illinois", "Indiana", "Iowa",
    "Kansas", "Kentucky", "Louisiana", "Maine", "Maryland", "Massachusetts", "Michigan",
    "Minnesota", "Mississippi", "Missouri", "Montana", "Nebraska", "Nevada", "New Hampshire",
    "New Jersey", "New Mexico", "New York", "North Carolina", "North Dakota", "Ohio",
    "Oklahoma", "Oregon", "Pennsylvania", "Rhode Island", "South Carolina", "South Dakota",
    "Tennessee", "Texas", "Utah", "Vermont", "Virginia", "Washington", "West Virginia",
    "Wisconsin", "Wyoming"
];

const roles = [
    "Assistant",
    "Registered Behavior Technician",
    "Analyst (BCaBA)",
    "Analyst (BCBA)",
    "Student Analyst"
];

const roleDescriptions = [
    {
        icon: User,
        title: "Assistant",
        description: "A direct therapist working in the field without the Registered Behavior Technician (RBT) certification, regardless of their experience."
    },
    {
        icon: Registered,
        title: "Registered Behavior Technician",
        description: "Currently active as a Certified Registered Behavior Technician with the BACB (Behavior Analysis Certification Board)."
    },
    {
        icon: Tie,
        title: "Analyst (BCaBA)",
        description: "Currently Certified as BCABA with the BACB (Behavior Analysis Certification Board)."
    },
    {
        icon: Tie,
        title: "Analyst (BCBA)",
        description: "Currently Certified as BCBA with the BACB (Behavior Analysis Certification Board)."
    },
    {
        icon: GraduationCap,
        title: "Student Analyst",
        description: "Enrolled in coursework working towards BCBA or BCaBA certification. You will apply as either Assistant or RBT and then upload your transcript for review. If your coursework is approved we will change your role to Student Analyst during the application process."
    }
];

const benefits = [
    {icon: null, label: "Sign On Bonus", value: "$1,500"},
    {icon: Clock, label: "Flexible Time Off"},
    {icon: PiggyBank, label: "401 K"},
    {icon: CoinsHand, label: "Weekly Pay"},
    {icon: Umbrella, label: "Vacation Credit"},
    {icon: Thermometer, label: "Sick Time"},
    {icon: MedicalCross, label: "Medical/Dental/Vision"},
    {icon: ShieldPlus, label: "Supplemental Insurance"},
    {icon: TrendUp, label: "Annual Retention Increase", subtext: "Eligible after one year"}
];

interface CompensationResult {
    salary: number;
    totalCompensation: number;
    annualBonus: number;
}

export function Form() {
    const [formData, setFormData] = useState<Partial<FormData>>({});
    const [errors, setErrors] = useState<Partial<Record<keyof FormData, string>>>({});
    const [result, setResult] = useState<CompensationResult | null>(null);

    const handleInputChange = (field: keyof FormData, value: string) => {
        setFormData(prev => ({...prev, [field]: value}));
        if (errors[field]) {
            setErrors(prev => ({...prev, [field]: undefined}));
        }
    };

    const handleCalculate = () => {
        const validation = Schema.safeParse(formData);
        if (!validation.success) {
            const fieldErrors: Partial<Record<keyof FormData, string>> = {};
            validation.error.errors.forEach(err => {
                const field = err.path[0] as keyof FormData;
                fieldErrors[field] = err.message;
            });
            setErrors(fieldErrors);
            return;
        }

        // Mock calculation - in real app this would call an API
        const baseSalaries: Record<string, number> = {
            "Assistant": 45000,
            "Registered Behavior Technician": 55000,
            "Analyst (BCaBA)": 70000,
            "Analyst (BCBA)": 80508,
            "Student Analyst": 50000
        };

        const salary = baseSalaries[formData.role || "Assistant"] || 50000;
        const annualBonus = salary * 0.1;
        const totalCompensation = salary + annualBonus;

        setResult({salary, totalCompensation, annualBonus});
    };

    const formatCurrency = (value: number) => {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 0,
            maximumFractionDigits: 2
        }).format(value);
    };

    return (
        <section className="bg-white">
            <div className="flex flex-col gap-16 lg:gap-20 items-center py-16 lg:py-20 px-5 lg:px-20 max-w-[1440px] mx-auto">
                {/* Header Section */}
                <div className="flex flex-col gap-8 lg:gap-12 items-center w-full max-w-[1280px]">
                    {/* Go Back Button */}
                    <div className="flex items-center w-full">
                        <Link
                            href="/careers/us"
                            className="flex items-center gap-2 text-primary hover:underline"
                        >
                            <ArrowLeft className="w-6 h-6"/>
                            <span className="text-lg lg:text-xl font-semibold tracking-[0.5px] underline">
                                Go back
                            </span>
                        </Link>
                    </div>

                    {/* Title & Description */}
                    <div className="flex flex-col gap-2 items-center text-center w-full max-w-[800px]">
                        <h1 className="text-3xl md:text-4xl lg:text-[54px] font-bold text-[#021b24] uppercase leading-[1.1]">
                            Compensation Estimator
                        </h1>
                        <p className="text-base lg:text-lg font-normal text-[#021b24] leading-[1.4]">
                            Unlock the potential of your future earnings with our easy-to-use Compensation Estimator.
                            Simply enter your basic information, including your role and location, and our calculator
                            will provide you with a personalized estimate of your potential compensation.
                        </p>
                    </div>
                </div>

                {/* Calculator Form */}
                <div className="flex flex-col gap-8 lg:gap-12 items-center w-full max-w-[900px]">
                    <h2 className="text-3xl lg:text-[42px] font-bold text-[#021b24] text-center leading-[1.1]">
                        Calculator
                    </h2>

                    <div className="flex flex-col gap-6 w-full">
                        {/* First Name */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                First Name *
                            </label>
                            <input
                                type="text"
                                value={formData.firstName || ''}
                                onChange={(e) => handleInputChange('firstName', e.target.value)}
                                className="w-full h-12 px-6 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary"
                                placeholder="Enter your first name"
                            />
                            {errors.firstName && (
                                <span className="text-red-500 text-sm">{errors.firstName}</span>
                            )}
                        </div>

                        {/* Last Name */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                Last Name *
                            </label>
                            <input
                                type="text"
                                value={formData.lastName || ''}
                                onChange={(e) => handleInputChange('lastName', e.target.value)}
                                className="w-full h-12 px-6 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary"
                                placeholder="Enter your last name"
                            />
                            {errors.lastName && (
                                <span className="text-red-500 text-sm">{errors.lastName}</span>
                            )}
                        </div>

                        {/* Email */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                Email Address **
                            </label>
                            <input
                                type="email"
                                value={formData.email || ''}
                                onChange={(e) => handleInputChange('email', e.target.value)}
                                className="w-full h-12 px-6 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary"
                                placeholder="Enter your email address"
                            />
                            {errors.email && (
                                <span className="text-red-500 text-sm">{errors.email}</span>
                            )}
                        </div>

                        {/* State & County */}
                        <div className="flex flex-col lg:flex-row gap-6 w-full">
                            <div className="flex flex-col gap-2 flex-1">
                                <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                    State *
                                </label>
                                <div className="relative">
                                    <select
                                        value={formData.state || ''}
                                        onChange={(e) => handleInputChange('state', e.target.value)}
                                        className="w-full h-12 px-6 pr-12 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary appearance-none bg-white"
                                    >
                                        <option value="">Select a state</option>
                                        {states.map(state => (
                                            <option key={state} value={state}>{state}</option>
                                        ))}
                                    </select>
                                    <ChevronDown
                                        className="absolute right-4 top-1/2 -translate-y-1/2 w-6 h-6 text-[#021b24] pointer-events-none"/>
                                </div>
                                {errors.state && (
                                    <span className="text-red-500 text-sm">{errors.state}</span>
                                )}
                            </div>

                            <div className="flex flex-col gap-2 flex-1">
                                <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                    County *
                                </label>
                                <div className="relative">
                                    <select
                                        value={formData.county || ''}
                                        onChange={(e) => handleInputChange('county', e.target.value)}
                                        className="w-full h-12 px-6 pr-12 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary appearance-none bg-white"
                                    >
                                        <option value="">Select a county</option>
                                        <option value="Palm Beach">Palm Beach</option>
                                        <option value="Miami-Dade">Miami-Dade</option>
                                        <option value="Broward">Broward</option>
                                    </select>
                                    <ChevronDown
                                        className="absolute right-4 top-1/2 -translate-y-1/2 w-6 h-6 text-[#021b24] pointer-events-none"/>
                                </div>
                                {errors.county && (
                                    <span className="text-red-500 text-sm">{errors.county}</span>
                                )}
                            </div>
                        </div>

                        {/* Role */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                Role *
                            </label>
                            <div className="relative">
                                <select
                                    value={formData.role || ''}
                                    onChange={(e) => handleInputChange('role', e.target.value)}
                                    className="w-full h-12 px-6 pr-12 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary appearance-none bg-white"
                                >
                                    <option value="">Select a role</option>
                                    {roles.map(role => (
                                        <option key={role} value={role}>{role}</option>
                                    ))}
                                </select>
                                <ChevronDown
                                    className="absolute right-4 top-1/2 -translate-y-1/2 w-6 h-6 text-[#021b24] pointer-events-none"/>
                            </div>
                            {errors.role && (
                                <span className="text-red-500 text-sm">{errors.role}</span>
                            )}
                        </div>

                        {/* Email Consent */}
                        <p className="text-base text-[#021b24] leading-[1.4]">
                            ** By providing your email address, you consent to receive future communications from PBS
                            regarding career opportunities.
                        </p>
                    </div>

                    {/* Calculate Button */}
                    <div className="flex items-center w-full">
                        <button
                            onClick={handleCalculate}
                            className="h-14 px-6 bg-secondary text-white text-lg lg:text-xl font-semibold tracking-[0.5px] rounded-full hover:bg-secondary/90 transition-colors w-full lg:w-[264px]"
                        >
                            Calculate
                        </button>
                    </div>
                </div>

                {/* Role Descriptions Section */}
                <div className="flex flex-col gap-12 lg:gap-[72px] items-center w-full max-w-[900px]">
                    <h2 className="text-3xl lg:text-[42px] font-bold text-[#021b24] text-center leading-[1.1]">
                        Role Descriptions
                    </h2>

                    <div className="flex flex-wrap gap-8 lg:gap-12 w-full">
                        {roleDescriptions.map((role, index) => (
                            <div
                                key={index}
                                className="flex flex-col gap-4 flex-1 min-w-[200px] max-w-[268px]"
                            >
                                <div className="w-12 h-12 bg-[#f2f7fb] rounded-full flex items-center justify-center">
                                    <role.icon className="w-6 h-6 text-primary"/>
                                </div>
                                <div className="flex flex-col gap-2">
                                    <h3 className="text-lg lg:text-xl font-bold text-[#021b24] tracking-[0.5px] leading-[1.2]">
                                        {role.title}
                                    </h3>
                                    <p className="text-base text-[#021b24] leading-[1.4]">
                                        {role.description}
                                    </p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Potential Compensation Results Section */}
                {result && (
                    <div className="flex flex-col gap-12 lg:gap-[72px] items-center w-full max-w-[1060px] bg-[#f2f7fb] p-10 lg:p-20">
                        <h2 className="text-3xl lg:text-[42px] font-bold text-[#021b24] text-center leading-[1.1]">
                            Your Potential Compensation
                        </h2>

                        {/* Compensation Cards */}
                        <div className="flex flex-col lg:flex-row gap-6 items-center w-full max-w-[900px]">
                            {/* Salary Card */}
                            <div
                                className="relative flex-1 min-h-[200px] lg:min-h-[270px] rounded-lg overflow-hidden shadow-[0px_0px_8px_2px_rgba(0,0,0,0.05)]">
                                <Image
                                    src="/images/careers/compensation-estimator/salary.jpg"
                                    alt=""
                                    fill
                                    className="object-cover"
                                />
                                <div
                                    className="absolute inset-0"
                                    style={{
                                        backgroundImage: 'linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.5) 100%), linear-gradient(90deg, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%)'
                                    }}
                                />
                                <div
                                    className="relative z-10 flex flex-col gap-4 items-center justify-center h-full p-6 lg:p-9">
                                    <div
                                        className="w-12 h-12 bg-white/40 backdrop-blur-[1px] rounded-full flex items-center justify-center shadow-[1px_1px_1px_0px_rgba(0,0,0,0.15)]">
                                        <MoneyWavy className="w-6 h-6 text-white"/>
                                    </div>
                                    <div className="flex flex-col gap-2 items-center text-white text-center">
                                        <p className="text-2xl lg:text-[32px] font-bold leading-[1.1] drop-shadow-[1px_1px_1px_rgba(0,0,0,0.15)]">
                                            {formatCurrency(result.salary)}
                                        </p>
                                        <p className="text-lg lg:text-xl font-bold tracking-[0.5px] drop-shadow-[1px_1px_1px_rgba(0,0,0,0.15)]">
                                            Salary
                                        </p>
                                    </div>
                                </div>
                            </div>

                            {/* Total Compensation Card (Center - Larger) */}
                            <div
                                className="relative w-full lg:w-auto lg:flex-[1.5] min-h-[250px] lg:min-h-[270px] rounded-lg overflow-hidden shadow-[0px_0px_8px_2px_rgba(0,0,0,0.05)]">
                                <Image
                                    src="/images/careers/compensation-estimator/total-potential-compensator.jpg"
                                    alt=""
                                    fill
                                    className="object-cover"
                                />
                                <div
                                    className="absolute inset-0"
                                    style={{
                                        backgroundImage: 'linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.5) 100%), linear-gradient(90deg, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%)'
                                    }}
                                />
                                <div
                                    className="relative z-10 flex flex-col gap-4 items-center justify-end h-full p-8 lg:p-12">
                                    <div
                                        className="w-16 h-16 bg-white/40 backdrop-blur-[1px] rounded-full flex items-center justify-center shadow-[1px_1px_1px_0px_rgba(0,0,0,0.15)]">
                                        <Calculator className="w-8 h-8 text-white"/>
                                    </div>
                                    <div className="flex flex-col gap-2 items-center text-white text-center">
                                        <p className="text-3xl lg:text-[42px] font-bold leading-[1.1]">
                                            {formatCurrency(result.totalCompensation)}
                                        </p>
                                        <p className="text-lg lg:text-xl font-bold tracking-[0.5px]">
                                            Total<br/>Potential Compensation
                                        </p>
                                    </div>
                                </div>
                            </div>

                            {/* Annual Bonus Card */}
                            <div
                                className="relative flex-1 min-h-[200px] lg:min-h-[270px] rounded-lg overflow-hidden shadow-[0px_0px_8px_2px_rgba(0,0,0,0.05)]">
                                <Image
                                    src="/images/careers/compensation-estimator/potential-annual-bonus.jpg"
                                    alt=""
                                    fill
                                    className="object-cover"
                                />
                                <div
                                    className="absolute inset-0"
                                    style={{
                                        backgroundImage: 'linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.5) 100%), linear-gradient(90deg, rgba(0, 0, 0, 0.2) 0%, rgba(0, 0, 0, 0.2) 100%)'
                                    }}
                                />
                                <div
                                    className="relative z-10 flex flex-col gap-4 items-center justify-center h-full p-6 lg:p-9">
                                    <div
                                        className="w-12 h-12 bg-white/40 backdrop-blur-[1px] rounded-full flex items-center justify-center shadow-[1px_1px_1px_0px_rgba(0,0,0,0.15)]">
                                        <Gift className="w-6 h-6 text-white"/>
                                    </div>
                                    <div className="flex flex-col gap-2 items-center text-white text-center">
                                        <p className="text-2xl lg:text-[32px] font-bold leading-[1.1] drop-shadow-[1px_1px_1px_rgba(0,0,0,0.15)]">
                                            {formatCurrency(result.annualBonus)}
                                        </p>
                                        <p className="text-lg lg:text-xl font-bold tracking-[0.5px] drop-shadow-[1px_1px_1px_rgba(0,0,0,0.15)]">
                                            Potential<br/>Annual Bonus
                                        </p>
                                    </div>
                                </div>
                            </div>
                        </div>

                        {/* Plus Benefits Section */}
                        <h2 className="text-3xl lg:text-[42px] font-bold text-[#021b24] text-center leading-[1.1]">
                            Plus Benefits
                        </h2>

                        <div className="flex flex-col gap-12 items-center w-full">
                            <div className="flex flex-wrap gap-x-6 gap-y-12 justify-center w-full">
                                {benefits.map((benefit, index) => (
                                    <div
                                        key={index}
                                        className="flex flex-col gap-4 items-center flex-1 min-w-[240px]"
                                    >
                                        {benefit.value ? (
                                            <div
                                                className="h-12 px-6 bg-primary rounded-full flex items-center justify-center">
                                                <span
                                                    className="text-lg lg:text-xl font-bold text-white tracking-[0.5px]">
                                                    {benefit.value}
                                                </span>
                                            </div>
                                        ) : benefit.icon && (
                                            <div
                                                className="w-12 h-12 bg-primary rounded-full flex items-center justify-center">
                                                <benefit.icon className="w-6 h-6 text-white"/>
                                            </div>
                                        )}
                                        <div className="flex flex-col items-center">
                                            <p className="text-lg lg:text-xl font-bold text-[#021b24] text-center tracking-[0.5px]">
                                                {benefit.label}
                                            </p>
                                            {benefit.subtext && (
                                                <p className="text-base text-[#021b24]/60">
                                                    {benefit.subtext}
                                                </p>
                                            )}
                                        </div>
                                    </div>
                                ))}
                            </div>

                            {/* Disclaimer */}
                            <div className="flex flex-col gap-2 text-center text-[#021b24]/60 w-full">
                                <p className="text-base">
                                    Estimate created on: {new Date().toLocaleDateString('en-US')}
                                </p>
                                <p className="text-xs leading-[1.4]">
                                    Disclaimer: Rate and salaries are subject to change at any time. Nothing herein
                                    should be construed as guaranteeing employment and does not change the at will
                                    nature of employment with Positive Behavior Supports Corp. Rates and salaries are
                                    non-negotiable. If you are offered employment with PBS Corp, your offer letter will
                                    include the rate/salary offered to you so please refer to that letter for further
                                    details. As part of our ongoing commitment to fostering career growth, PBS offers an
                                    automatic 5% annual raise to non-exempt employees on their employment anniversary
                                    date. This raise is applied each year for up to five years. The goal of this program
                                    is to recognize long term service and encourage continued professional development.
                                </p>
                            </div>

                            {/* Apply Now Button */}
                            <Link
                                href="/careers/us"
                                className="h-14 px-6 bg-secondary text-white text-lg lg:text-xl font-semibold tracking-[0.5px] rounded-full hover:bg-secondary/90 transition-colors flex items-center justify-center"
                            >
                                Apply Now
                            </Link>
                        </div>
                    </div>
                )}
            </div>
        </section>
    );
}

