"use client";

import {z} from "zod";
import {useState} from "react";
import Link from "next/link";
import Image from "next/image";
import {ArrowLeft, ChevronDown} from "lucide-react";
import {User} from "@/shared/icones/user";
import {Registered} from "@/shared/icones/registered";
import {Tie} from "@/shared/icones/tie";
import {GraduationCap} from "@/shared/icones/graduation-cap";
import {Clock} from "@/shared/icones/clock";
import {CoinsHand} from "@/shared/icones/coins-hand";
import {Umbrella} from "@/shared/icones/umbrella";
import {Thermometer} from "@/shared/icones/thermometer";
import {MedicalCross} from "@/shared/icones/medical-cross";
import {ShieldPlus} from "@/shared/icones/shield-plus";
import {TrendUp} from "@/shared/icones/trend-up";
import {Calculator} from "@/shared/icones/calculator";
import {Gift} from "@/shared/icones/gift";
import {MoneyWavy} from "@/shared/icones/money-wavy";
import {PiggyBank} from "@/shared/icones/piggy-bank";

const Schema = z.object({
    firstName: z.string()
        .min(2, {message: "First name must be at least 2 characters long"})
        .max(50, {message: "First name must be less than 50 characters long"}),
    lastName: z.string()
        .min(2, {message: "Last name must be at least 2 characters long"})
        .max(50, {message: "Last name must be less than 50 characters long"}),
    email: z.string().email(),
    state: z.string().min(1, {message: "Please select a state"}),
    county: z.string().min(1, {message: "Please select a county"}),
    role: z.string().min(1, {message: "Please select a role"})
});

type FormData = z.infer<typeof Schema>;

const states = [
    "Alabama", "Alaska", "Arizona", "Arkansas", "California", "Colorado", "Connecticut",
    "Delaware", "Florida", "Georgia", "Hawaii", "Idaho", "Illinois", "Indiana", "Iowa",
    "Kansas", "Kentucky", "Louisiana", "Maine", "Maryland", "Massachusetts", "Michigan",
    "Minnesota", "Mississippi", "Missouri", "Montana", "Nebraska", "Nevada", "New Hampshire",
    "New Jersey", "New Mexico", "New York", "North Carolina", "North Dakota", "Ohio",
    "Oklahoma", "Oregon", "Pennsylvania", "Rhode Island", "South Carolina", "South Dakota",
    "Tennessee", "Texas", "Utah", "Vermont", "Virginia", "Washington", "West Virginia",
    "Wisconsin", "Wyoming"
];

const roles = [
    "Assistant",
    "Registered Behavior Technician",
    "Analyst (BCaBA)",
    "Analyst (BCBA)",
    "Student Analyst"
];

const roleDescriptions = [
    {
        icon: User,
        title: "Assistant",
        description: "A direct therapist working in the field without the Registered Behavior Technician (RBT) certification, regardless of their experience."
    },
    {
        icon: Registered,
        title: "Registered Behavior Technician",
        description: "Currently active as a Certified Registered Behavior Technician with the BACB (Behavior Analysis Certification Board)."
    },
    {
        icon: Tie,
        title: "Analyst (BCaBA)",
        description: "Currently Certified as BCABA with the BACB (Behavior Analysis Certification Board)."
    },
    {
        icon: Tie,
        title: "Analyst (BCBA)",
        description: "Currently Certified as BCBA with the BACB (Behavior Analysis Certification Board)."
    },
    {
        icon: GraduationCap,
        title: "Student Analyst",
        description: "Enrolled in coursework working towards BCBA or BCaBA certification. You will apply as either Assistant or RBT and then upload your transcript for review. If your coursework is approved we will change your role to Student Analyst during the application process."
    }
];

const benefits = [
    {icon: null, label: "Sign On Bonus", value: "$1,500"},
    {icon: Clock, label: "Flexible Time Off"},
    {icon: PiggyBank, label: "401 K"},
    {icon: CoinsHand, label: "Weekly Pay"},
    {icon: Umbrella, label: "Vacation Credit"},
    {icon: Thermometer, label: "Sick Time"},
    {icon: MedicalCross, label: "Medical/Dental/Vision"},
    {icon: ShieldPlus, label: "Supplemental Insurance"},
    {icon: TrendUp, label: "Annual Retention Increase", subtext: "Eligible after one year"}
];

interface CompensationResult {
    salary: number;
    totalCompensation: number;
    annualBonus: number;
}

export function Form() {
    const [formData, setFormData] = useState<Partial<FormData>>({});
    const [errors, setErrors] = useState<Partial<Record<keyof FormData, string>>>({});
    const [result, setResult] = useState<CompensationResult | null>(null);

    const handleInputChange = (field: keyof FormData, value: string) => {
        setFormData(prev => ({...prev, [field]: value}));
        if (errors[field]) {
            setErrors(prev => ({...prev, [field]: undefined}));
        }
    };

    const handleCalculate = () => {
        const validation = Schema.safeParse(formData);
        if (!validation.success) {
            const fieldErrors: Partial<Record<keyof FormData, string>> = {};
            validation.error.errors.forEach(err => {
                const field = err.path[0] as keyof FormData;
                fieldErrors[field] = err.message;
            });
            setErrors(fieldErrors);
            return;
        }

        // Mock calculation - in real app this would call an API
        const baseSalaries: Record<string, number> = {
            "Assistant": 45000,
            "Registered Behavior Technician": 55000,
            "Analyst (BCaBA)": 70000,
            "Analyst (BCBA)": 80508,
            "Student Analyst": 50000
        };

        const salary = baseSalaries[formData.role || "Assistant"] || 50000;
        const annualBonus = salary * 0.1;
        const totalCompensation = salary + annualBonus;

        setResult({salary, totalCompensation, annualBonus});
    };

    const formatCurrency = (value: number) => {
        return new Intl.NumberFormat('en-US', {
            style: 'currency',
            currency: 'USD',
            minimumFractionDigits: 0,
            maximumFractionDigits: 2
        }).format(value);
    };

    return (
        <section className="bg-white">
            <div className="flex flex-col gap-16 lg:gap-20 items-center py-16 lg:py-20 px-5 lg:px-20 max-w-[1440px] mx-auto">
                {/* Header Section */}
                <div className="flex flex-col gap-8 lg:gap-12 items-center w-full max-w-[1280px]">
                    {/* Go Back Button */}
                    <div className="flex items-center w-full">
                        <Link
                            href="/careers/us"
                            className="flex items-center gap-2 text-primary hover:underline"
                        >
                            <ArrowLeft className="w-6 h-6"/>
                            <span className="text-lg lg:text-xl font-semibold tracking-[0.5px] underline">
                                Go back
                            </span>
                        </Link>
                    </div>

                    {/* Title & Description */}
                    <div className="flex flex-col gap-2 items-center text-center w-full max-w-[800px]">
                        <h1 className="text-3xl md:text-4xl lg:text-[54px] font-bold text-[#021b24] uppercase leading-[1.1]">
                            Compensation Estimator
                        </h1>
                        <p className="text-base lg:text-lg font-normal text-[#021b24] leading-[1.4]">
                            Unlock the potential of your future earnings with our easy-to-use Compensation Estimator.
                            Simply enter your basic information, including your role and location, and our calculator
                            will provide you with a personalized estimate of your potential compensation.
                        </p>
                    </div>
                </div>

                {/* Calculator Form */}
                <div className="flex flex-col gap-8 lg:gap-12 items-center w-full max-w-[900px]">
                    <h2 className="text-3xl lg:text-[42px] font-bold text-[#021b24] text-center leading-[1.1]">
                        Calculator
                    </h2>

                    <div className="flex flex-col gap-6 w-full">
                        {/* First Name */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                First Name *
                            </label>
                            <input
                                type="text"
                                value={formData.firstName || ''}
                                onChange={(e) => handleInputChange('firstName', e.target.value)}
                                className="w-full h-12 px-6 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary"
                                placeholder="Enter your first name"
                            />
                            {errors.firstName && (
                                <span className="text-red-500 text-sm">{errors.firstName}</span>
                            )}
                        </div>

                        {/* Last Name */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                Last Name *
                            </label>
                            <input
                                type="text"
                                value={formData.lastName || ''}
                                onChange={(e) => handleInputChange('lastName', e.target.value)}
                                className="w-full h-12 px-6 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary"
                                placeholder="Enter your last name"
                            />
                            {errors.lastName && (
                                <span className="text-red-500 text-sm">{errors.lastName}</span>
                            )}
                        </div>

                        {/* Email */}
                        <div className="flex flex-col gap-2 w-full">
                            <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
                                Email Address **
                            </label>
                            <input
                                type="email"
                                value={formData.email || ''}
                                onChange={(e) => handleInputChange('email', e.target.value)}
                                className="w-full h-12 px-6 border-2 border-[#e4e7e9] rounded-full text-base text-[#021b24] focus:outline-none focus:border-primary"
                                placeholder="Enter your email address"
                            />
                            {errors.email && (
                                <span className="text-red-500 text-sm">{errors.email}</span>
                            )}
                        </div>

                        {/* State & County */}
                        <div className="flex flex-col lg:flex-row gap-6 w-full">
                            <div className="flex flex-col gap-2 flex-1">
                                <label className="text-sm font-medium text-[#021b24] tracking-[0.5px]">
